import React, { Component } from "react";

class DogList extends Component {
  constructor(props) {
    super(props)
    this.state = { dogs: [] }
    this.fetchDogs()
  }

  //somewhere that is "global"
  // something I can change
  // has to tell our component to call the render method again 

  fetchDogs = () => {
    fetch("http://localhost:4000/dogs")
      .then(resp => resp.json())
      .then(data => this.setState({ dogs: data }))

  }

  render() {
    console.log("are you mounting: ", this.state)
    return <div className="dogContainer">Something</div>;
  }
}

export default DogList;
